mapper.select:
SELECT  *
  FROM HOSPITAL_MEMBER
 WHERE HOSPITAL_ID = #{hospital_id}

 	AND DEPT = #{dept}

SELECT  DISTINCT DEPT
  FROM HOSPITAL_MEMBER
 WHERE HOSPITAL_ID = #{hospital_id}

mapper.insert:
INSERT 
INTO HOSPITAL_MEMBER(
	MEMBER_ID
   ,HOSPITAL_ID
   ,NAME
   ,DEPT
)
VALUES(
	(SELECT MAX(CONVERT(MEMBER_ID,INTEGER))+1 FROM HOSPITAL_MEMBER)
   ,#{hospital_id}
   ,#{name}
   ,#{dept}
)
