mapper.select:
SELECT A.RESERVE_NO ,A.MEMBER_ID ,A.HOSPITAL_MEMBER_ID ,FORMATDATETIME(A.RESERVE_DATE, 'yyyy-MM-dd') AS RESERVE_DATE ,FORMATDATETIME(A.RESERVE_DATE, 'HH') AS RESERVE_TIME ,A.ACCEPT ,A.SYMPTOM ,B.NAME ,C.NAME AS DOCTOR_NAME ,C.DEPT AS DOCTOR_DEPT FROM RESERVATION A JOIN MEMBER B ON A.MEMBER_ID = B.ID JOIN HOSPITAL_MEMBER C ON C.MEMBER_ID= A.HOSPITAL_MEMBER_ID JOIN HOSPITAL D ON D.ID = C.HOSPITAL_ID WHERE 1=1
AND D.ID = #{hospitalId}
AND A.ACCEPT = #{accept}
ORDER BY RESERVE_DATE DESC

mapper.update:
UPDATE RESERVATION
   SET ACCEPT = #{accept}
WHERE RESERVE_NO = #{reserve_no}
